mapper.select:
SELECT ichannelId, ichannelLevel, chl.label,
FROM stat.tab_city_tvprogram_rounds_week T32 INNER JOIN dim_channel chl ON T32.ichannelid = chl.chid WHERE T32.ichannellevel IN (1, 2) AND iareaid = #{areaId}
AND tv.subject IN
#{subjectId}
GROUP BY ichannelid, ichannellevel, chl. LABEL ORDER BY AVG (avg_c_0_rate) IS NULL, rate DESC, ichannelid

SELECT ID, idDetail, AVG(rate) rate, round(AVG(rate000), 3) rate000 FROM ( SELECT UNNEST (ARRAY [ 1, 2, 3, 4, 5, 6, 7 ]) AS ID, UNNEST ( ARRAY [ '4-14', '15-24', '25-34', '35-44', '45-54', '55-64', '65+' ] ) AS idDetail, UNNEST (avg_c_age_rate) rate, UNNEST (avg_c_age_rate000) rate000 FROM stat.tab_city_tvprogram_rounds_week T32 LEFT JOIN basicinfo.tab_tv_info tv ON T32.tvid = tv.tvid AND tvtype = 1 WHERE 1 = 1 AND iareaid = #{areaId} AND ichannelid IN (#{channelId})
AND tv.subject IN
#{subjectId}
) mytable GROUP BY ID, idDetail ORDER BY ID

SELECT ID, idDetail, AVG(rate) rate, round(AVG(rate000), 3) rate000 FROM ( SELECT UNNEST (ARRAY [ 1, 2, 3, 4 ]) AS ID, UNNEST ( ARRAY [ '0-2000', '2000-4000', '4000-6000', '6000+' ] ) AS idDetail, UNNEST (avg_c_earn_rate) rate, UNNEST (avg_c_earn_rate000) rate000 FROM stat.tab_city_tvprogram_rounds_week T32 LEFT JOIN basicinfo.tab_tv_info tv ON T32.tvid = tv.tvid AND tvtype = 1 WHERE 1 = 1 AND iareaid = #{areaId} AND ichannelid IN (#{channelId})
AND tv.subject IN
#{subjectId}
) mytable GROUP BY ID, idDetail ORDER BY ID

SELECT ID, idDetail, AVG(rate) rate, round(AVG(rate000), 3) rate000 FROM ( SELECT UNNEST (ARRAY [ 1, 2, 3, 4, 5 ]) AS ID, UNNEST ( ARRAY [ '未受过正规教育', '小学', '初中', '高中', '大学及以上' ] ) AS idDetail, UNNEST (avg_c_edu_rate) rate, UNNEST (avg_c_edu_rate000) rate000 FROM stat.tab_city_tvprogram_rounds_week T32 LEFT JOIN basicinfo.tab_tv_info tv ON T32.tvid = tv.tvid AND tvtype = 1 WHERE 1 = 1 AND iareaid = #{areaId} AND ichannelid IN (#{channelId})
AND tv.subject IN
#{subjectId}
) mytable GROUP BY ID, idDetail ORDER BY ID

SELECT AVG (avg_c_0_rate) AS rate FROM stat.tab_city_tvprogram_rounds_week T32 LEFT JOIN basicinfo.tab_tv_info tv ON T32.tvid = tv.tvid AND tvtype = 1 WHERE 1 = 1 AND T32.iareaid = #{areaId}
AND ichannelid IN (#{channelId})
AND tv.subject IN
#{subjectId}
AND T32.avg_c_0_rate IS NOT NULL
