mapper.update:
UPDATE tab_play_mark
SET word = #{word},
	word_x = #{word_x},
	description = #{description},
	createtime = #{createTime}
WHERE id = #{id}

mapper.select:
SELECT id, word, word_x, description
FROM tab_play_mark
WHERE 1 = 1

  AND round_id = #{roundId}


  AND word = #{word}


  AND word_x = #{word_x}

ORDER BY createtime asc

SELECT tpm.id, tpm.word, tpm.word_x, tpm.description, tpr.id as 'playRound.id', tpr.seriesno as 'playRound.seriesNo', tpr.roundno as 'playRound.roundNo' FROM tab_play_mark tpm, tab_play_round tpr WHERE 1 = 1 AND tpm.round_id = tpr.id
AND tpm.project_id = #{projectId}
ORDER BY tpm.createtime asc
